%% Coherence Analysis
N  = 2048;
fs = 16000;
c  = 343;
f = linspace(0,fs,N); % Frequency slot
numEle = (floor(length(Audio_Channel(1,:))/N)*N);
FrameData1 = reshape(Audio_Channel(1,1:numEle),N ,[]) ;  % 1024 bytes frame

Coh = zeros(1,length(FrameData1(1,:)),N/2+1);
test = zeros(8,8,N/2+1);
idex=0;
win = hanning(N);
for iMic=1:8
    for jMic=1:8
       FrameData1 = reshape(Audio_Channel(iMic,1:numEle),N ,[]) ;  % 1024 bytes frame
       FrameData2 = reshape(Audio_Channel(jMic,1:numEle),N ,[]) ;  % 1024 bytes frame
       idex=0;
       for i=1:length(FrameData1(1,:))
           %Coh(i,:) =  Coherence(FrameData1(:,i),FrameData2(:,i))    ;
           [Coh(1,i,:),f1] = mscohere(FrameData1(:,i),FrameData2(:,i),[],[],N,fs);
           if (i>10)
               test(iMic,jMic,:) = test(iMic,jMic,:) + sqrt(Coh(1,i,:));
               idex = idex+1;
           end
       end 
       test(iMic,jMic,:) = test(iMic,jMic,:)/idex;
    end
end
