%% Energy Analysic
% 4 is average of noise
% Max of speech can reach 52
N = 1024;
fs = 16000;
c = 343;

FrameData = reshape(Audio_Channel1(1:(floor(length(Audio_Channel1)/N)*N)),N ,[]) ;  % 1024 bytes frame
Power = zeros(length(FrameData(1,:)),1);


for i=1:length(FrameData(1,:))
    Power(i,1) =  sqrt(sum(FrameData(:,i).*FrameData(:,i)))/N;    
end

for i=1:length(Audio_Channel1)-N
    Audio_Channel2(i) = Power(floor(i/N)+1);
end
