%% Offline Beamforming and Wiener filter
global Audio_Data_Old;
global Out_Sum_Pre1;
global Out_Sum_Pre2; 
global Audio_Sum_Half_Old;
global win

win = [zeros(1,512) hann(1024)' zeros(1,512)];
N = 1024;
fs = 16000;
c = 343;
m = 8;
BeamOut = [];
%W = BeamFormingSD_Init(audio_data);
a = load('W_ZP.mat');
W = a.W;
Audio_Data_Old = zeros(8,1024);
Audio_Sum_Half_Old = zeros(1,512);
Out_Sum_Pre1  = zeros(1,2048);
Out_Sum_Pre2  = zeros(1,2048);
OFFSET = 6;
N=1024;
Shift78 =	linspace(1,N,N);
Shift78 =   sinc(Shift78-(1/(4))*(3)-512);
Frame1Old = [0 0];
Frame2Old = 0;
Frame3Old = 0;
Frame4Old = 0;
Frame5Old = 0;
Frame6Old = 0;
Frame7Old = [0 0 0 0 0 0];
Frame8Old = [0 0 0 0 0 0];



for i=1:N:(length(Audio_Channel(1,:))-N)
    FrameData = Audio_Channel(:,i:i+N-1);
    Frames_D = FrameData; 
    Frame1 =    FrameData(1, end-1:end);
    Frame2 =    FrameData(2, end);
    Frame3 =    FrameData(3, end);
    Frame4 =    FrameData(4, end);
    Frame5 =    FrameData(5, end);
    Frame6 =    FrameData(6, end);
    Frame7 =    FrameData(7,end-(OFFSET-1):end);
    Frame8 =    FrameData(8,end-(OFFSET-1):end);
    
    temp = FrameData(7, 1:(N-OFFSET));
    Frames_D(7,:) = [Frame7Old temp];
    Delay = conv(Frames_D(7,:), Shift78); % Delay 1/2 sample
    Frames_D(7,:) = Delay(512:1023+512);

    temp = FrameData(8, 1:(N-OFFSET));
    Frames_D(8,:) = [Frame8Old temp];
    Delay = conv(Frames_D(8,:), Shift78); % Delay 1/2 sample
    Frames_D(8,:) = Delay(512:1023+512);

    temp = FrameData(1,1 :(N -  2)); % delay 2 sample
    Frames_D(1,:) = [Frame1Old temp]; % channel 1 faster 2 samples to channel2

    temp = FrameData(3, 1:N - 1);         % Delay 1 sample
    Frames_D(3,:) = [Frame3Old temp];  % channel 3 faster 1 samples to channel1
    
    
    temp = FrameData(5,1 :N - 1);  % delay 1 sample
    Frames_D(5,:) = [Frame5Old temp];
    Frame1Old = Frame1;
    Frame2Old = Frame2;
    Frame3Old = Frame3;
    Frame4Old = Frame4;
    Frame5Old = Frame5;
    Frame6Old = Frame6;
    Frame7Old = Frame7;
    Frame8Old = Frame8;
    
    Beam_Audio = BeamFormingSD(Frames_D,0,W);
    BeamOut = [BeamOut Beam_Audio];
end

